{"title":"ElastiFlow: Flows (client AS/server AS) - Vega (directed graph)","type":"vega","aggs":[],"params":{"spec":"{
  "$schema": "https://vega.github.io/schema/vega/v5.json",
  "description": "Copyright (C)2022 ElastiFlow Inc.",
  "autosize": "fit",

  "data": [
    {
      "name": "flows",
      "url": {
        "%context%": true,
        "%timefield%": "@timestamp",
        "index": "elastiflow-flow-codex-*",
        "body": {
          "size": 0,
          "aggs": {
            "table": {
              "composite": {
                "size": 384,
                "sources": [{
                    "client": {
                      "terms": {
                        "field": "flow.client.as.label"
                      }
                    }
                  },
                  {
                    "server": {
                      "terms": {
                        "field": "flow.server.as.label"
                      }
                    }
                  }
                ]
              },
              "aggs": {
                "flow_bytes": {
                  "sum": {
                    "field": "flow.bytes"
                  }
                },
                "flow_packets": {
                  "sum": {
                    "field": "flow.packets"
                  }
                }
              }
            }
          }
        }
      },
      "format": {
        "property": "aggregations.table.buckets"
      },
      "transform": [
        {
          "type": "formula",
          "expr": "datum.key.client",
          "as": "source"
        },
        {
          "type": "formula",
          "expr": "datum.key.server",
          "as": "target"
        },
        {
          "type": "formula",
          "expr": "datum.flow_bytes.value",
          "as": "bytes"
        },
        {
          "type": "formula",
          "expr": "datum.flow_packets.value",
          "as": "packets"
        }
      ]
    },

    {
      "name": "endpoints",
      "source": "flows",
      "transform": [
        {
          "type": "fold",
          "fields": ["source", "target"],
          "as": ["stack", "key"]
        },
        {
          "type": "aggregate",
          "groupby": ["key"],
          "fields": ["doc_count", "bytes", "packets"],
          "ops": ["sum", "sum", "sum"],
          "as": ["doc_count", "bytes", "packets"]
        }
      ]
    },

    {
      "name": "traffic",
      "source": "flows",
      "transform": [
        {
          "type": "aggregate",
          "groupby": ["source", "target"],
          "fields": ["doc_count", "bytes", "packets"],
          "ops": ["sum", "sum", "sum"],
          "as": ["doc_count", "bytes", "packets"]
        }
      ]
    }
  ],

  "scales": [
    {
      "name": "endpointBytesScale",
      "type": "quantile",
      "domain": {"data": "endpoints", "field": "bytes"},
      "range": [128,192,256,320,384,448,512,576,640,768,896,1024]
    },
    {
      "name": "fontsizeEndpointBytesScale",
      "type": "quantile",
      "domain": {"data": "endpoints", "field": "bytes"},
      "range": [0,0,0,0,0,0,0,12]
    },
    {
      "name": "colorEndpointsBytesBlues",
      "type": "quantile",
      "domain": {"data": "endpoints", "field": "bytes"},
      "range": ["#99ddff", "#80d4ff", "#66ccff", "#33bbff", "#00aaff", "#0099e6"]
    },
    {
      "name": "colorEndpointsBytesLightBlues",
      "type": "quantile",
      "domain": {"data": "endpoints", "field": "bytes"},
      "range": ["#e6f7ff", "#cceeff", "#b3e6ff", "#99ddff"]
    },
    {
      "name": "trafficBytesScale",
      "type": "quantile",
      "domain": {"data": "traffic", "field": "bytes"},
      "range": [1,2,3,4,6,8]
    },
    {
      "name": "colorTrafficBytesGreys",
      "type": "quantile",
      "domain": {"data": "traffic", "field": "bytes"},
      "range": ["#ddd", "#ccc", "#bbb", "#aaa", "#999", "#888"]
    }
  ],

  "signals": [
    {
      "name": "cx",
      "update": "width / 2"
    },
    {
      "name": "cy",
      "update": "height / 2"
    },
    {
      "name": "nodeCharge",
      "value": -128,
      "bind": {"input": "range", "min": -200, "max": 10, "step": 1}
    },
    {
      "name": "linkDistance",
      "value": 18,
      "bind": {"input": "range", "min": 8, "max": 64, "step": 1}
    },
    {
      "name": "decay",
      "value": 0.5,
      "bind": {"input": "range", "min": 0, "max": 1, "step": 0.01}
    },
    {
      "name": "gravityX",
      "value": 0.1,
      "bind": {"input": "range", "min": 0, "max": 1}
    },
    {
      "name": "gravityY",
      "value": 0.2,
      "bind": {"input": "range", "min": 0, "max": 1}
    },
    {
      "name": "static",
      "value": false,
      "bind": {"input": "checkbox"}
    },
    {
      "name": "fix",
      "value": false,
      "on": [
        {
          "events": "symbol:mouseout[!event.buttons], window:mouseup",
          "update": "false"
        },
        {
          "events": "symbol:mouseover",
          "update": "fix || true"
        },
        {
          "events": "[symbol:mousedown, window:mouseup] > window:mousemove!",
          "update": "xy()",
          "force": true
        }
      ]
    },
    {
      "name": "node",
      "value": null,
      "on": [
        {
          "events": "symbol:mouseover",
          "update": "fix === true ? item() : node"
        }
      ]
    },
    {
      "name": "restart",
      "value": false,
      "on": [
        {
          "events": {"signal": "fix"}, "update": "fix && fix.length"
        }
      ]
    }
  ],

  "marks": [
    {
      "name": "nodes",
      "type": "symbol",
      "zindex": 1,
      "from": {"data": "endpoints"},
      "on": [
        {
          "trigger": "fix",
          "modify": "node",
          "values": "fix === true ? {fx: node.x, fy: node.y} : {fx: fix[0], fy: fix[1]}"
        },
        {"trigger": "!fix", "modify": "node", "values": "{fx: null, fy: null}"}
      ],
      "encode": {
        "enter": {
          "fill": {"scale": "colorEndpointsBytesBlues", "field": "bytes"},
          "stroke": {"scale": "colorEndpointsBytesLightBlues", "field": "bytes"},
          "xfocus": {"signal": "cx"},
          "yfocus": {"signal": "cy"}
        },
        "update": {
          "size": {"scale": "endpointBytesScale", "field": "bytes"},
          "cursor": {"value": "pointer"}
        },
        "hover": {
          "tooltip": {
            "signal": "datum.key + ' (Bytes: ' + format(datum.bytes, '.2s') + ', Packets: ' + datum.packets + ', Records: ' + datum.doc_count + ')'"
          }
        }
      },
      "transform": [
        {
          "type": "force",
          "iterations": 160,
          "velocityDecay": {
            "signal": "decay"
          },
          "restart": {"signal": "restart"},
          "static": {"signal": "static"},
          "signal": "force",
          "forces": [
            {"force": "center", "x": {"signal": "cx"}, "y": {"signal": "cy"}},
            {"force": "x", "x": "xfocus", "strength": {"signal": "gravityX"}},
            {"force": "y", "y": "yfocus", "strength": {"signal": "gravityY"}},
            {"force": "collide", "radius": {"signal": "linkDistance"}},
            {"force": "nbody", "strength": {"signal": "nodeCharge"}},
            {"force": "link", "links": "traffic", "id": "datum.key", "distance": {"signal": "linkDistance"}}
          ]
        }
      ]
    },
    {
      "type": "text",
      "zindex": 2,
      "from": {"data": "nodes"},
      "interactive": false,
      "encode": {
        "enter": {
          "fill": {"value": "black"},
          "fontSize": {"scale": "fontsizeEndpointBytesScale", "field": "datum.bytes"}
        },
        "update": {
          "y": {"field": "y", "offset": {"signal": "sqrt(datum.size/2) * 1.3"}},
          "x": {"field": "x"},
          "text": {"field": "datum.key"},
          "align": {"value": "center"}
        }
      }
    },
    {
      "name": "paths",
      "type": "path",
      "from": {"data": "traffic"},
      "interactive": true,
      "encode": {
        "update": {
          "stroke": {"scale": "colorTrafficBytesGreys", "field": "bytes"},
          "strokeWidth": {"scale": "trafficBytesScale", "field": "bytes"}
        },
        "hover": {
          "tooltip": {
            "signal": "'Bytes: ' + format(datum.bytes, '.2s') + ', Packets: ' + datum.packets + ', Records: ' + datum.doc_count"
          }
        }
      },
      "transform": [
        {
          "type": "linkpath",
          "require": {"signal": "force"},
          "shape": "curve",
          "sourceX": "datum.source.x",
          "sourceY": "datum.source.y",
          "targetX": "datum.target.x",
          "targetY": "datum.target.y"
        }
      ]
    }
  ]
}"}}

